import{_ as t,o as a,c as l,ag as i}from"./chunks/framework.DhRkhqU-.js";const y=JSON.parse('{"title":"Linux – DevOps Mühendisleri İçin Temeller","description":"","frontmatter":{},"headers":[],"relativePath":"01-Linux/01-Basic/01-Introduction.md","filePath":"01-Linux/01-Basic/01-Introduction.md"}'),r={name:"01-Linux/01-Basic/01-Introduction.md"};function n(o,e,s,d,c,u){return a(),l("div",null,[...e[0]||(e[0]=[i('<h1 id="linux-–-devops-muhendisleri-icin-temeller" tabindex="-1">Linux – DevOps Mühendisleri İçin Temeller <a class="header-anchor" href="#linux-–-devops-muhendisleri-icin-temeller" aria-label="Permalink to &quot;Linux – DevOps Mühendisleri İçin Temeller&quot;">​</a></h1><h2 id="genel-bakıs" tabindex="-1">Genel Bakış <a class="header-anchor" href="#genel-bakıs" aria-label="Permalink to &quot;Genel Bakış&quot;">​</a></h2><p>Linux, dünyadaki sunucuların, süper bilgisayarların ve bulut altyapısının büyük çoğunluğuna güç veren açık kaynaklı işletim sistemi çekirdeğidir. Bir DevOps mühendisi için Linux sadece bir işletim sistemi değil; otomasyon, konteynerizasyon ve orkestrasyon için evrensel bir çalışma ortamıdır.</p><h2 id="devops-icin-neden-onemli" tabindex="-1">DevOps İçin Neden Önemli? <a class="header-anchor" href="#devops-icin-neden-onemli" aria-label="Permalink to &quot;DevOps İçin Neden Önemli?&quot;">​</a></h2><ul><li><strong>Infrastructure as Code (IaC):</strong> Çoğu IaC aracı (Terraform, Ansible) ve CI/CD pipeline&#39;ları Linux ajanları üzerinde çalışır.</li><li><strong>Konteynerizasyon:</strong> Docker ve Kubernetes, Linux&#39;a özgüdür (native). Cgroups ve namespace mantığını anlamak, konteynerleri debug etmek için şarttır.</li><li><strong>Sunucu Kararlılığı:</strong> Linux, tüketici işletim sistemlerine kıyasla üstün çalışma süresi (uptime), kaynak yönetimi ve güvenlik sunar.</li><li><strong>Bulut Hakimiyeti:</strong> AWS, Azure ve GCP ortamları ağırlıklı olarak Linux tabanlıdır.</li></ul><h2 id="temel-kavramlar" tabindex="-1">Temel Kavramlar <a class="header-anchor" href="#temel-kavramlar" aria-label="Permalink to &quot;Temel Kavramlar&quot;">​</a></h2><h3 id="_1-kernel-cekirdek" tabindex="-1">1. Kernel (Çekirdek) <a class="header-anchor" href="#_1-kernel-cekirdek" aria-label="Permalink to &quot;1. Kernel (Çekirdek)&quot;">​</a></h3><p>Donanım ve yazılım arasındaki temel arayüzdür. CPU, bellek ve çevresel aygıtları yönetir.</p><ul><li><strong>User Space:</strong> Uygulamaların ve kullanıcı süreçlerinin çalıştığı yer.</li><li><strong>Kernel Space:</strong> Temel işletim sistemi fonksiyonlarının tam yetkiyle çalıştığı yer.</li></ul><h3 id="_2-shell-kabuk" tabindex="-1">2. Shell (Kabuk) <a class="header-anchor" href="#_2-shell-kabuk" aria-label="Permalink to &quot;2. Shell (Kabuk)&quot;">​</a></h3><p>Kullanıcı komutlarını yorumlayan komut satırı arayüzüdür (CLI).</p><ul><li><strong>Bash (Bourne Again SHell):</strong> Çoğu dağıtım için standarttır.</li><li><strong>Zsh:</strong> Etkileşimli kullanım için popülerdir (macOS&#39;ta varsayılan).</li><li><strong>Stdin/Stdout/Stderr:</strong> Giriş, çıkış ve hata loglaması için üç standart akış.</li></ul><h3 id="_3-dosya-sistemi-hiyerarsisi-fhs" tabindex="-1">3. Dosya Sistemi Hiyerarşisi (FHS) <a class="header-anchor" href="#_3-dosya-sistemi-hiyerarsisi-fhs" aria-label="Permalink to &quot;3. Dosya Sistemi Hiyerarşisi (FHS)&quot;">​</a></h3><p>Linux&#39;ta her şey bir dosyadır.</p><ul><li><code>/</code>: Kök (Root) dizini.</li><li><code>/bin</code> &amp; <code>/usr/bin</code>: Temel kullanıcı komutları (ls, cp, cat).</li><li><code>/etc</code>: Yapılandırma (Config) dosyaları.</li><li><code>/var</code>: Değişken veriler (loglar, spool dosyaları).</li><li><code>/home</code>: Kullanıcı ev dizinleri.</li><li><code>/proc</code> &amp; <code>/sys</code>: Sistem durumunu temsil eden sanal dosya sistemleri.</li></ul><h3 id="_4-izinler-ve-sahiplik" tabindex="-1">4. İzinler ve Sahiplik <a class="header-anchor" href="#_4-izinler-ve-sahiplik" aria-label="Permalink to &quot;4. İzinler ve Sahiplik&quot;">​</a></h3><p>Sahip (u), Grup (g) ve Diğerleri (o) için Okuma (r), Yazma (w) ve Çalıştırma (x) tabanlı güvenlik modeli.</p><h3 id="_5-surec-process-yonetimi" tabindex="-1">5. Süreç (Process) Yönetimi <a class="header-anchor" href="#_5-surec-process-yonetimi" aria-label="Permalink to &quot;5. Süreç (Process) Yönetimi&quot;">​</a></h3><p>Çalışan her program, benzersiz bir Süreç Kimliğine (PID) sahip bir süreçtir.</p><ul><li><strong>Daemon:</strong> Arka plan süreci (servis).</li><li><strong>Zombie:</strong> Ebeveyni tarafından çıkış durumu okunmayı bekleyen ölü süreç.</li></ul><h2 id="temel-komutlar-ozet" tabindex="-1">Temel Komutlar (Özet) <a class="header-anchor" href="#temel-komutlar-ozet" aria-label="Permalink to &quot;Temel Komutlar (Özet)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Komut</th><th style="text-align:left;">Açıklama</th><th style="text-align:left;">Örnek</th></tr></thead><tbody><tr><td style="text-align:left;"><code>ls</code></td><td style="text-align:left;">Dizin içeriğini listele</td><td style="text-align:left;"><code>ls -lah</code></td></tr><tr><td style="text-align:left;"><code>cd</code></td><td style="text-align:left;">Dizin değiştir</td><td style="text-align:left;"><code>cd /var/log</code></td></tr><tr><td style="text-align:left;"><code>cp</code></td><td style="text-align:left;">Kopyala</td><td style="text-align:left;"><code>cp -r kaynak/ hedef/</code></td></tr><tr><td style="text-align:left;"><code>mv</code></td><td style="text-align:left;">Taşı veya yeniden adlandır</td><td style="text-align:left;"><code>mv eski.txt yeni.txt</code></td></tr><tr><td style="text-align:left;"><code>rm</code></td><td style="text-align:left;">Sil</td><td style="text-align:left;"><code>rm -rf /tmp/cache</code></td></tr><tr><td style="text-align:left;"><code>grep</code></td><td style="text-align:left;">Metin ara</td><td style="text-align:left;"><code>grep &quot;ERROR&quot; server.log</code></td></tr><tr><td style="text-align:left;"><code>chmod</code></td><td style="text-align:left;">İzinleri değiştir</td><td style="text-align:left;"><code>chmod +x script.sh</code></td></tr><tr><td style="text-align:left;"><code>chown</code></td><td style="text-align:left;">Sahipliği değiştir</td><td style="text-align:left;"><code>chown user:group dosya</code></td></tr><tr><td style="text-align:left;"><code>sudo</code></td><td style="text-align:left;">Yönetici olarak çalıştır</td><td style="text-align:left;"><code>sudo apt update</code></td></tr></tbody></table><h2 id="en-iyi-uygulamalar-best-practices" tabindex="-1">En İyi Uygulamalar (Best Practices) <a class="header-anchor" href="#en-iyi-uygulamalar-best-practices" aria-label="Permalink to &quot;En İyi Uygulamalar (Best Practices)&quot;">​</a></h2><ol><li><strong>En Az Yetki (Least Privilege):</strong> Uygulamaları asla <code>root</code> olarak çalıştırmayın. Özel servis kullanıcıları oluşturun.</li><li><strong>Değişmez Altyapı (Immutable Infrastructure):</strong> Canlı sunucuları yamalamaktan kaçının; onları yeni imajlarla değiştirin.</li><li><strong>Her Şeyi Scriptleyin:</strong> Bir komutu iki kez çalıştırıyorsanız, bir script yazın.</li><li><strong>Log Rotasyonu:</strong> Disk dolmasını önlemek için <code>logrotate</code> yapılandırmasının olduğundan emin olun.</li><li><strong>SSH Anahtarları:</strong> Şifre ile girişi kapatın; erişim için SSH anahtarları kullanın.</li></ol>',24)])])}const h=t(r,[["render",n]]);export{y as __pageData,h as default};
