import{_ as a,o as l,c as o,ag as r}from"./chunks/framework.DhRkhqU-.js";const k=JSON.parse('{"title":"Docker CLI Komutları","description":"","frontmatter":{},"headers":[],"relativePath":"02-Docker/04-Commands-CLI.md","filePath":"02-Docker/04-Commands-CLI.md"}'),i={name:"02-Docker/04-Commands-CLI.md"};function n(t,e,d,c,m,u){return l(),o("div",null,[...e[0]||(e[0]=[r('<h1 id="docker-cli-komutları" tabindex="-1">Docker CLI Komutları <a class="header-anchor" href="#docker-cli-komutları" aria-label="Permalink to &quot;Docker CLI Komutları&quot;">​</a></h1><h2 id="_1-imaj-image-komutları" tabindex="-1">1. İmaj (Image) Komutları <a class="header-anchor" href="#_1-imaj-image-komutları" aria-label="Permalink to &quot;1. İmaj (Image) Komutları&quot;">​</a></h2><ul><li><code>docker build -t myapp:v1 .</code>: Dockerfile&#39;dan imaj oluştur.</li><li><code>docker images</code>: İndirilmiş/oluşturulmuş imajları listele.</li><li><code>docker rmi &lt;image_id&gt;</code>: İmajı sil.</li><li><code>docker pull nginx</code>: Docker Hub&#39;dan imaj indir.</li><li><code>docker tag myapp:v1 myuser/myapp:latest</code>: İmajanı etiketle (tagle).</li><li><code>docker push myuser/myapp:latest</code>: İmajı Registry&#39;e gönder.</li></ul><h2 id="_2-konteyner-container-komutları" tabindex="-1">2. Konteyner (Container) Komutları <a class="header-anchor" href="#_2-konteyner-container-komutları" aria-label="Permalink to &quot;2. Konteyner (Container) Komutları&quot;">​</a></h2><ul><li><code>docker run -d -p 80:80 nginx</code>: Konteyneri arka planda (<code>-d</code>) ve port yönlendirmesiyle (<code>-p</code>) çalıştır.</li><li><code>docker ps</code>: Çalışan konteynerleri listele.</li><li><code>docker ps -a</code>: Durmuş olanlar dahil hepsini listele.</li><li><code>docker stop &lt;container_id&gt;</code>: Konteyneri durdur.</li><li><code>docker rm &lt;container_id&gt;</code>: Konteyneri sil (Önce durdurulmalı).</li><li><code>docker rm -f &lt;container_id&gt;</code>: Zorla durdur ve sil.</li></ul><h2 id="_3-debug-ve-inceleme" tabindex="-1">3. Debug ve İnceleme <a class="header-anchor" href="#_3-debug-ve-inceleme" aria-label="Permalink to &quot;3. Debug ve İnceleme&quot;">​</a></h2><ul><li><code>docker logs &lt;container_id&gt;</code>: Konteynerin loglarını gör. (<code>-f</code> ile canlı takip et).</li><li><code>docker exec -it &lt;container_id&gt; /bin/bash</code>: Çalışan konteynerin içine terminal ile gir. (Alpine için <code>/bin/sh</code> kullanın).</li><li><code>docker inspect &lt;container_id&gt;</code>: Tüm detaylı konfigürasyonu (IP, Volume, Env) JSON olarak gör.</li><li><code>docker stats</code>: Canlı CPU ve RAM kullanımını gör.</li></ul><h2 id="_4-temizlik-prune" tabindex="-1">4. Temizlik (Prune) <a class="header-anchor" href="#_4-temizlik-prune" aria-label="Permalink to &quot;4. Temizlik (Prune)&quot;">​</a></h2><p>Disk dolduğunda hayat kurtarır.</p><ul><li><code>docker system prune</code>: Durmuş konteynerleri, kullanılmayan ağları ve sarkan (dangling) imajları siler.</li><li><code>docker system prune -a</code>: <strong>Kullanılmayan tüm imajları</strong> da siler (Dikkat!).</li><li><code>docker volume prune</code>: Kullanılmayan volume&#39;leri siler.</li></ul>',10)])])}const p=a(i,[["render",n]]);export{k as __pageData,p as default};
