import{_ as e,o as l,c as r,ag as n}from"./chunks/framework.DhRkhqU-.js";const f=JSON.parse('{"title":"OSI & TCP/IP Modelleri: Temeller","description":"","frontmatter":{},"headers":[],"relativePath":"08-Network/01-OSI-TCP-IP.md","filePath":"08-Network/01-OSI-TCP-IP.md"}'),a={name:"08-Network/01-OSI-TCP-IP.md"};function s(o,t,i,d,g,y){return l(),r("div",null,[...t[0]||(t[0]=[n('<h1 id="osi-tcp-ip-modelleri-temeller" tabindex="-1">OSI &amp; TCP/IP Modelleri: Temeller <a class="header-anchor" href="#osi-tcp-ip-modelleri-temeller" aria-label="Permalink to &quot;OSI &amp; TCP/IP Modelleri: Temeller&quot;">​</a></h1><h2 id="_1-osi-modeli-open-systems-interconnection" tabindex="-1">1. OSI Modeli (Open Systems Interconnection) <a class="header-anchor" href="#_1-osi-modeli-open-systems-interconnection" aria-label="Permalink to &quot;1. OSI Modeli (Open Systems Interconnection)&quot;">​</a></h2><p>Ağ etkileşimlerini 7 katmanda anlamak için kullanılan kavramsal bir çerçevedir.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Katman</th><th style="text-align:left;">İsim</th><th style="text-align:left;">Veri Birimi</th><th style="text-align:left;">İşlev</th><th style="text-align:left;">DevOps İlgisi</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>7</strong></td><td style="text-align:left;"><strong>Application</strong> (Uygulama)</td><td style="text-align:left;">Veri</td><td style="text-align:left;">Ağ sürecinden uygulamaya (HTTP, SSH, FTP).</td><td style="text-align:left;"><strong>Yüksek</strong>: API&#39;ler, Web Sunucuları.</td></tr><tr><td style="text-align:left;"><strong>6</strong></td><td style="text-align:left;"><strong>Presentation</strong> (Sunum)</td><td style="text-align:left;">Veri</td><td style="text-align:left;">Veri gösterimi ve şifreleme (SSL/TLS).</td><td style="text-align:left;"><strong>Orta</strong>: Kodlama (Encoding), Serileştirme.</td></tr><tr><td style="text-align:left;"><strong>5</strong></td><td style="text-align:left;"><strong>Session</strong> (Oturum)</td><td style="text-align:left;">Veri</td><td style="text-align:left;">Hostlar arası iletişim.</td><td style="text-align:left;"><strong>Düşük</strong>: RPC, Soketler.</td></tr><tr><td style="text-align:left;"><strong>4</strong></td><td style="text-align:left;"><strong>Transport</strong> (Taşıma)</td><td style="text-align:left;">Segment</td><td style="text-align:left;">Uçtan uca bağlantılar ve güvenilirlik (TCP/UDP).</td><td style="text-align:left;"><strong>Yüksek</strong>: Portlar, Yük Dengeleme.</td></tr><tr><td style="text-align:left;"><strong>3</strong></td><td style="text-align:left;"><strong>Network</strong> (Ağ)</td><td style="text-align:left;">Paket</td><td style="text-align:left;">Yol belirleme ve IP adresleme (Routerlar).</td><td style="text-align:left;"><strong>Yüksek</strong>: IP, Subnetler, VPC&#39;ler.</td></tr><tr><td style="text-align:left;"><strong>2</strong></td><td style="text-align:left;"><strong>Data Link</strong> (Veri Bağlantısı)</td><td style="text-align:left;">Çerçeve (Frame)</td><td style="text-align:left;">Fiziksel adresleme (MAC, Switchler).</td><td style="text-align:left;"><strong>Düşük</strong>: ARP, VLAN&#39;lar (çoğunlukla bulut yönetir).</td></tr><tr><td style="text-align:left;"><strong>1</strong></td><td style="text-align:left;"><strong>Physical</strong> (Fiziksel)</td><td style="text-align:left;">Bit</td><td style="text-align:left;">Medya, sinyal ve ikili iletim.</td><td style="text-align:left;"><strong>Düşük</strong>: Kablolar, Wifi (Bulut bunu soyutlar).</td></tr></tbody></table><blockquote><p><strong>Akılda Tutma Yöntemi</strong>: <strong>P</strong>lease <strong>D</strong>o <strong>N</strong>ot <strong>T</strong>hrow <strong>S</strong>ausage <strong>P</strong>izza <strong>A</strong>way.</p></blockquote><hr><h2 id="_2-tcp-ip-modeli" tabindex="-1">2. TCP/IP Modeli <a class="header-anchor" href="#_2-tcp-ip-modeli" aria-label="Permalink to &quot;2. TCP/IP Modeli&quot;">​</a></h2><p>İnternet tarafından kullanılan pratik modeldir, OSI&#39;yi 4 katmana indirger.</p><ol><li><strong>Application</strong> (OSI 5, 6, 7): HTTP, DNS, SSH.</li><li><strong>Transport</strong> (OSI 4): TCP, UDP.</li><li><strong>Internet</strong> (OSI 3): IP, ICMP.</li><li><strong>Network Access</strong> (OSI 1, 2): Ethernet, Wi-Fi.</li></ol><hr><h2 id="_3-tcp-vs-udp-katman-4" tabindex="-1">3. TCP vs UDP (Katman 4) <a class="header-anchor" href="#_3-tcp-vs-udp-katman-4" aria-label="Permalink to &quot;3. TCP vs UDP (Katman 4)&quot;">​</a></h2><h3 id="tcp-transmission-control-protocol" tabindex="-1">TCP (Transmission Control Protocol) <a class="header-anchor" href="#tcp-transmission-control-protocol" aria-label="Permalink to &quot;TCP (Transmission Control Protocol)&quot;">​</a></h3><ul><li><strong>Bağlantı odaklı (Connection-oriented)</strong>: Veri göndermeden önce bir oturum kurar.</li><li><strong>Güvenilir (Reliable)</strong>: Teslimatı (ACK&#39;lar) ve sırayı garanti eder.</li><li><strong>Ağır (Heavy)</strong>: Ek yük (overhead) nedeniyle daha yavaştır.</li><li><strong>Kullanım Alanları</strong>: Web (HTTP), E-posta (SMTP), Dosya Transferi (FTP), SSH.</li></ul><p><strong>3 Yönlü El Sıkışma (3-Way Handshake)</strong>:</p><ol><li><strong>SYN</strong>: İstemci sorar &quot;Bağlanabilir miyim?&quot;</li><li><strong>SYN-ACK</strong>: Sunucu der ki &quot;Evet, hazırım.&quot;</li><li><strong>ACK</strong>: İstemci der ki &quot;Tamam, şimdi bağlanıyorum.&quot;</li></ol><h3 id="udp-user-datagram-protocol" tabindex="-1">UDP (User Datagram Protocol) <a class="header-anchor" href="#udp-user-datagram-protocol" aria-label="Permalink to &quot;UDP (User Datagram Protocol)&quot;">​</a></h3><ul><li><strong>Bağlantısız (Connectionless)</strong>: Gönder ve unut.</li><li><strong>Güvenilmez (Unreliable)</strong>: Teslimat veya sıra garantisi yoktur.</li><li><strong>Hızlı (Fast)</strong>: Düşük ek yük.</li><li><strong>Kullanım Alanları</strong>: Yayın (Streaming), VoIP, DNS sorguları, Oyun.</li></ul><hr><h2 id="_4-yaygın-portlar" tabindex="-1">4. Yaygın Portlar <a class="header-anchor" href="#_4-yaygın-portlar" aria-label="Permalink to &quot;4. Yaygın Portlar&quot;">​</a></h2><p>Bunları ezberlemelisiniz.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Port</th><th style="text-align:left;">Protokol</th><th style="text-align:left;">Servis</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>20/21</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;">FTP (Dosya Transferi)</td></tr><tr><td style="text-align:left;"><strong>22</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;"><strong>SSH</strong> (Güvenli Kabuk) - <em>Kritik</em></td></tr><tr><td style="text-align:left;"><strong>53</strong></td><td style="text-align:left;">TCP/UDP</td><td style="text-align:left;"><strong>DNS</strong> (Alan Adı Sistemi)</td></tr><tr><td style="text-align:left;"><strong>80</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;"><strong>HTTP</strong> (Web)</td></tr><tr><td style="text-align:left;"><strong>443</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;"><strong>HTTPS</strong> (Güvenli Web)</td></tr><tr><td style="text-align:left;"><strong>3306</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;">MySQL / MariaDB</td></tr><tr><td style="text-align:left;"><strong>5432</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;">PostgreSQL</td></tr><tr><td style="text-align:left;"><strong>6379</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;">Redis</td></tr><tr><td style="text-align:left;"><strong>8080</strong></td><td style="text-align:left;">TCP</td><td style="text-align:left;">Yaygın HTTP Alternatifi (Tomcat, Jenkins)</td></tr></tbody></table>',21)])])}const P=e(a,[["render",s]]);export{f as __pageData,P as default};
