import{_ as i,o as r,c as t,ag as a}from"./chunks/framework.DhRkhqU-.js";const g=JSON.parse('{"title":"DNS, HTTP & HTTPS: Web Katmanı","description":"","frontmatter":{},"headers":[],"relativePath":"08-Network/02-DNS-HTTP-HTTPS.md","filePath":"08-Network/02-DNS-HTTP-HTTPS.md"}'),l={name:"08-Network/02-DNS-HTTP-HTTPS.md"};function o(n,e,s,d,u,m){return r(),t("div",null,[...e[0]||(e[0]=[a('<h1 id="dns-http-https-web-katmanı" tabindex="-1">DNS, HTTP &amp; HTTPS: Web Katmanı <a class="header-anchor" href="#dns-http-https-web-katmanı" aria-label="Permalink to &quot;DNS, HTTP &amp; HTTPS: Web Katmanı&quot;">​</a></h1><h2 id="_1-dns-domain-name-system" tabindex="-1">1. DNS (Domain Name System) <a class="header-anchor" href="#_1-dns-domain-name-system" aria-label="Permalink to &quot;1. DNS (Domain Name System)&quot;">​</a></h2><p>&quot;İnternetin Telefon Rehberi&quot;. İnsanların okuyabildiği isimleri (<code>google.com</code>) IP adreslerine (<code>142.250.1.1</code>) çevirir.</p><h3 id="nasıl-calısır-basitlestirilmis" tabindex="-1">Nasıl Çalışır (Basitleştirilmiş) <a class="header-anchor" href="#nasıl-calısır-basitlestirilmis" aria-label="Permalink to &quot;Nasıl Çalışır (Basitleştirilmiş)&quot;">​</a></h3><ol><li><strong>Tarayıcı</strong>: Yerel önbelleği (cache) kontrol eder.</li><li><strong>İşletim Sistemi</strong>: <code>/etc/hosts</code> dosyasını kontrol eder.</li><li><strong>Resolver</strong>: İSS&#39;nin (ISP) DNS sunucusuna sorar.</li><li><strong>Root Server</strong>: TLD sunucusuna yönlendirir (<code>.com</code>).</li><li><strong>TLD Server</strong>: Yetkili İsim Sunucusuna (Authoritative Name Server) yönlendirir (örn: AWS Route53).</li><li><strong>Authoritative Server</strong>: IP adresini döndürür.</li></ol><h3 id="yaygın-kayıt-turleri" tabindex="-1">Yaygın Kayıt Türleri <a class="header-anchor" href="#yaygın-kayıt-turleri" aria-label="Permalink to &quot;Yaygın Kayıt Türleri&quot;">​</a></h3><ul><li><strong>A</strong>: Hostname -&gt; IPv4 (<code>1.2.3.4</code>).</li><li><strong>AAAA</strong>: Hostname -&gt; IPv6.</li><li><strong>CNAME</strong>: Hostname -&gt; Hostname (Takma Ad). <em>Kök domainde (@) kullanılamaz.</em></li><li><strong>MX</strong>: Mail Exchange (E-posta).</li><li><strong>TXT</strong>: Metin notları (SPF, doğrulama).</li><li><strong>NS</strong>: Name Server (Bu bölgeyi kim yönetiyor?).</li></ul><hr><h2 id="_2-http-hypertext-transfer-protocol" tabindex="-1">2. HTTP (HyperText Transfer Protocol) <a class="header-anchor" href="#_2-http-hypertext-transfer-protocol" aria-label="Permalink to &quot;2. HTTP (HyperText Transfer Protocol)&quot;">​</a></h2><p>Web&#39;in protokolü. İstek/Yanıt (Request/Response) modeli.</p><h3 id="http-metotları-fiiller" tabindex="-1">HTTP Metotları (Fiiller) <a class="header-anchor" href="#http-metotları-fiiller" aria-label="Permalink to &quot;HTTP Metotları (Fiiller)&quot;">​</a></h3><ul><li><strong>GET</strong>: Veri getir. (Idempotent - Tekrarlanabilir).</li><li><strong>POST</strong>: Yeni veri oluştur.</li><li><strong>PUT</strong>: Veriyi güncelle/değiştir. (Idempotent).</li><li><strong>PATCH</strong>: Kısmi güncelleme.</li><li><strong>DELETE</strong>: Veriyi sil.</li></ul><h3 id="http-durum-kodları" tabindex="-1">HTTP Durum Kodları <a class="header-anchor" href="#http-durum-kodları" aria-label="Permalink to &quot;HTTP Durum Kodları&quot;">​</a></h3><ul><li><strong>2xx (Başarılı)</strong>: <ul><li><code>200 OK</code>: Standart başarı.</li><li><code>201 Created</code>: Kaynak oluşturuldu.</li></ul></li><li><strong>3xx (Yönlendirme)</strong>: <ul><li><code>301 Moved Permanently</code>: SEO dostu kalıcı yönlendirme.</li><li><code>302 Found</code>: Geçici yönlendirme.</li></ul></li><li><strong>4xx (İstemci Hatası)</strong>: <ul><li><code>400 Bad Request</code>: Geçersiz sözdizimi.</li><li><code>401 Unauthorized</code>: Kimlik doğrulama gerekli.</li><li><code>403 Forbidden</code>: Kimlik doğrulandı ama yetki yok.</li><li><code>404 Not Found</code>: Kaynak bulunamadı.</li></ul></li><li><strong>5xx (Sunucu Hatası)</strong>: <ul><li><code>500 Internal Server Error</code>: Uygulama çöktü.</li><li><code>502 Bad Gateway</code>: Üst sunucu (upstream) başarısız oldu (Nginx -&gt; App).</li><li><code>503 Service Unavailable</code>: Aşırı yüklenme veya bakım.</li><li><code>504 Gateway Timeout</code>: Üst sunucu çok uzun sürdü.</li></ul></li></ul><hr><h2 id="_3-https-guvenli" tabindex="-1">3. HTTPS (Güvenli) <a class="header-anchor" href="#_3-https-guvenli" aria-label="Permalink to &quot;3. HTTPS (Güvenli)&quot;">​</a></h2><p>SSL/TLS üzerinden HTTP. Veriyi iletim sırasında şifreler.</p><h3 id="tls-el-sıkısma-basitlestirilmis" tabindex="-1">TLS El Sıkışma (Basitleştirilmiş) <a class="header-anchor" href="#tls-el-sıkısma-basitlestirilmis" aria-label="Permalink to &quot;TLS El Sıkışma (Basitleştirilmiş)&quot;">​</a></h3><ol><li><strong>Client Hello</strong>: &quot;Ben TLS 1.2/1.3 destekliyorum&quot;.</li><li><strong>Server Hello</strong>: &quot;Hadi TLS 1.3 kullanalım. İşte Sertifikam.&quot;</li><li><strong>Doğrulama (Verification)</strong>: İstemci sertifikanın geçerli olup olmadığını kontrol eder (güvenilir CA tarafından verilmiş mi, süresi dolmuş mu).</li><li><strong>Anahtar Değişimi (Key Exchange)</strong>: Simetrik bir oturum anahtarı üzerinde anlaşırlar.</li><li><strong>Güvenli Oturum</strong>: Gelecekteki tüm veriler oturum anahtarı ile şifrelenir.</li></ol><h3 id="sertifikalar" tabindex="-1">Sertifikalar <a class="header-anchor" href="#sertifikalar" aria-label="Permalink to &quot;Sertifikalar&quot;">​</a></h3><ul><li><strong>CA (Certificate Authority)</strong>: Güvenilir otorite (örn: Let&#39;s Encrypt, DigiCert).</li><li><strong>Self-Signed</strong>: Geliştirme için iyidir, tarayıcı uyarılarını tetikler.</li><li><strong>Wildcard</strong>: <code>*.ornek.com</code>, <code>api.ornek.com</code> ve <code>www.ornek.com</code> adreslerini kapsar.</li></ul>',21)])])}const h=i(l,[["render",o]]);export{g as __pageData,h as default};
