import{_ as e,o as l,c as a,ag as n}from"./chunks/framework.DhRkhqU-.js";const y=JSON.parse('{"title":"Subnetting & CIDR: IP Yönetimi","description":"","frontmatter":{},"headers":[],"relativePath":"08-Network/03-Subnetting-CIDR.md","filePath":"08-Network/03-Subnetting-CIDR.md"}'),i={name:"08-Network/03-Subnetting-CIDR.md"};function r(d,t,o,s,c,g){return l(),a("div",null,[...t[0]||(t[0]=[n('<h1 id="subnetting-cidr-ip-yonetimi" tabindex="-1">Subnetting &amp; CIDR: IP Yönetimi <a class="header-anchor" href="#subnetting-cidr-ip-yonetimi" aria-label="Permalink to &quot;Subnetting &amp; CIDR: IP Yönetimi&quot;">​</a></h1><h2 id="_1-ip-adresleri-ipv4" tabindex="-1">1. IP Adresleri (IPv4) <a class="header-anchor" href="#_1-ip-adresleri-ipv4" aria-label="Permalink to &quot;1. IP Adresleri (IPv4)&quot;">​</a></h2><p>Dört adet 8-bitlik bloktan (oktet) oluşan 32-bitlik bir sayıdır. Örnek: <code>192.168.1.1</code></p><h3 id="public-vs-private-ip-ler-rfc-1918" tabindex="-1">Public vs Private IP&#39;ler (RFC 1918) <a class="header-anchor" href="#public-vs-private-ip-ler-rfc-1918" aria-label="Permalink to &quot;Public vs Private IP&#39;ler (RFC 1918)&quot;">​</a></h3><p>Adres tasarrufu sağlamak için, ağların içinde (LAN/VPC) özel (private) IP&#39;ler, internette ise genel (public) IP&#39;ler kullanırız.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Sınıf</th><th style="text-align:left;">Aralık</th><th style="text-align:left;">Kullanım Alanı</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Class A</strong></td><td style="text-align:left;"><code>10.0.0.0</code> - <code>10.255.255.255</code></td><td style="text-align:left;">Büyük Şirketler / Bulut VPC&#39;leri</td></tr><tr><td style="text-align:left;"><strong>Class B</strong></td><td style="text-align:left;"><code>172.16.0.0</code> - <code>172.31.255.255</code></td><td style="text-align:left;">AWS varsayılanı, Docker</td></tr><tr><td style="text-align:left;"><strong>Class C</strong></td><td style="text-align:left;"><code>192.168.0.0</code> - <code>192.168.255.255</code></td><td style="text-align:left;">Ev Routerları</td></tr></tbody></table><blockquote><p><strong>NAT (Network Address Translation)</strong>: Birden fazla özel IP&#39;nin tek bir genel IP (Gateway) üzerinden internete erişmesini sağlar.</p></blockquote><hr><h2 id="_2-cidr-classless-inter-domain-routing" tabindex="-1">2. CIDR (Classless Inter-Domain Routing) <a class="header-anchor" href="#_2-cidr-classless-inter-domain-routing" aria-label="Permalink to &quot;2. CIDR (Classless Inter-Domain Routing)&quot;">​</a></h2><p>IP aralıkları için standart gösterim: <code>IP/PrefixUzunluğu</code>. <strong>Prefix Uzunluğu</strong>, kaç bitin sabit olduğunu (Ağ Kimliği) söyler. Geri kalanı Hostlar içindir.</p><h3 id="yaygın-cidr-blokları" tabindex="-1">Yaygın CIDR Blokları <a class="header-anchor" href="#yaygın-cidr-blokları" aria-label="Permalink to &quot;Yaygın CIDR Blokları&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">CIDR</th><th style="text-align:left;">Toplam IP</th><th style="text-align:left;">Kullanılabilir IP (yaklaşık)</th><th style="text-align:left;">Kullanım Alanı</th></tr></thead><tbody><tr><td style="text-align:left;"><code>/32</code></td><td style="text-align:left;">1</td><td style="text-align:left;">1</td><td style="text-align:left;">Tek Host (Güvenlik duvarı kuralı)</td></tr><tr><td style="text-align:left;"><code>/30</code></td><td style="text-align:left;">4</td><td style="text-align:left;">2</td><td style="text-align:left;">Noktadan Noktaya (Point-to-Point) bağlantı</td></tr><tr><td style="text-align:left;"><code>/28</code></td><td style="text-align:left;">16</td><td style="text-align:left;">11-13</td><td style="text-align:left;">Çok küçük subnet</td></tr><tr><td style="text-align:left;"><code>/24</code></td><td style="text-align:left;">256</td><td style="text-align:left;">251-254</td><td style="text-align:left;">Standart LAN / Subnet</td></tr><tr><td style="text-align:left;"><code>/20</code></td><td style="text-align:left;">4,096</td><td style="text-align:left;">4,000+</td><td style="text-align:left;">Orta ölçekli VPC</td></tr><tr><td style="text-align:left;"><code>/16</code></td><td style="text-align:left;">65,536</td><td style="text-align:left;">65,000+</td><td style="text-align:left;">Büyük VPC (Standart AWS max)</td></tr><tr><td style="text-align:left;"><code>/0</code></td><td style="text-align:left;">Tüm IP&#39;ler</td><td style="text-align:left;">Hepsi</td><td style="text-align:left;">İnternet (<code>0.0.0.0/0</code>)</td></tr></tbody></table><h3 id="hesaplama-ornegi-24" tabindex="-1">Hesaplama Örneği: <code>/24</code> <a class="header-anchor" href="#hesaplama-ornegi-24" aria-label="Permalink to &quot;Hesaplama Örneği: `/24`&quot;">​</a></h3><ul><li><strong>IP</strong>: <code>192.168.1.0/24</code></li><li><strong>Maske</strong>: <code>255.255.255.0</code></li><li><strong>Ağ (Network)</strong>: <code>192.168.1.0</code> (Ayrılmış)</li><li><strong>Yayın (Broadcast)</strong>: <code>192.168.1.255</code> (Ayrılmış)</li><li><strong>Aralık</strong>: <code>192.168.1.1</code> - <code>192.168.1.254</code></li></ul><hr><h2 id="_3-devops-baglamı-vpc-tasarımı" tabindex="-1">3. DevOps Bağlamı: VPC Tasarımı <a class="header-anchor" href="#_3-devops-baglamı-vpc-tasarımı" aria-label="Permalink to &quot;3. DevOps Bağlamı: VPC Tasarımı&quot;">​</a></h2><p>Bir VPC (Sanal Özel Bulut) tasarlarken:</p><ol><li><strong>Çakışmayın (Don&#39;t overlap)</strong>: VPC CIDR&#39;ınızın (<code>10.0.0.0/16</code>), şirket içi ağınızla veya peer etmeyi planladığınız diğer VPC&#39;lerle çakışmadığından emin olun.</li><li><strong>Alt Ağlara Bölme (Subnetting)</strong>: VPC&#39;yi subnetlere bölün. <ul><li><code>10.0.1.0/24</code> -&gt; Public Subnet A</li><li><code>10.0.2.0/24</code> -&gt; Private Subnet A</li></ul></li><li><strong>Yer Ayırın</strong>: Tüm IP&#39;leri hemen kullanmayın.</li></ol>',18)])])}const f=e(i,[["render",r]]);export{y as __pageData,f as default};
