import{_ as e,o as t,c as l,ag as r}from"./chunks/framework.DhRkhqU-.js";const c=JSON.parse('{"title":"Yük Dengeleme (Load Balancing) & Proxyler: Trafik Yönetimi","description":"","frontmatter":{},"headers":[],"relativePath":"08-Network/04-LoadBalancing-Proxy.md","filePath":"08-Network/04-LoadBalancing-Proxy.md"}'),n={name:"08-Network/04-LoadBalancing-Proxy.md"};function i(o,a,s,d,u,g){return t(),l("div",null,[...a[0]||(a[0]=[r('<h1 id="yuk-dengeleme-load-balancing-proxyler-trafik-yonetimi" tabindex="-1">Yük Dengeleme (Load Balancing) &amp; Proxyler: Trafik Yönetimi <a class="header-anchor" href="#yuk-dengeleme-load-balancing-proxyler-trafik-yonetimi" aria-label="Permalink to &quot;Yük Dengeleme (Load Balancing) &amp; Proxyler: Trafik Yönetimi&quot;">​</a></h1><h2 id="_1-yuk-dengeleme-load-balancing" tabindex="-1">1. Yük Dengeleme (Load Balancing) <a class="header-anchor" href="#_1-yuk-dengeleme-load-balancing" aria-label="Permalink to &quot;1. Yük Dengeleme (Load Balancing)&quot;">​</a></h2><p>Güvenilirlik ve ölçeklenebilirlik sağlamak için gelen ağ trafiğini birden fazla sunucuya dağıtma işlemidir.</p><h3 id="katman-4-vs-katman-7" tabindex="-1">Katman 4 vs Katman 7 <a class="header-anchor" href="#katman-4-vs-katman-7" aria-label="Permalink to &quot;Katman 4 vs Katman 7&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Özellik</th><th style="text-align:left;">Katman 4 (Taşıma)</th><th style="text-align:left;">Katman 7 (Uygulama)</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Veri</strong></td><td style="text-align:left;">IP &amp; Port</td><td style="text-align:left;">HTTP Başlıkları, Çerezler, URL</td></tr><tr><td style="text-align:left;"><strong>Hız</strong></td><td style="text-align:left;">Daha Hızlı (Paket seviyesi)</td><td style="text-align:left;">Daha Yavaş (İçeriği inceler)</td></tr><tr><td style="text-align:left;"><strong>Kararlar</strong></td><td style="text-align:left;">&quot;TCP port 80&#39;i Sunucu A&#39;ya gönder&quot;</td><td style="text-align:left;">&quot;<code>/api</code>&#39;yi Servis A&#39;ya, <code>/img</code>&#39;yi Servis B&#39;ye gönder&quot;</td></tr><tr><td style="text-align:left;"><strong>Örnekler</strong></td><td style="text-align:left;">AWS Network Load Balancer (NLB)</td><td style="text-align:left;">AWS Application Load Balancer (ALB), Nginx</td></tr></tbody></table><h3 id="algoritmalar" tabindex="-1">Algoritmalar <a class="header-anchor" href="#algoritmalar" aria-label="Permalink to &quot;Algoritmalar&quot;">​</a></h3><ul><li><strong>Round Robin</strong>: Sıralı (A -&gt; B -&gt; C -&gt; A).</li><li><strong>Least Connections</strong>: En az aktif kullanıcısı olan sunucuya gönder.</li><li><strong>IP Hash</strong>: İstemci IP&#39;si her zaman aynı sunucuya gider (Sticky Sessions).</li></ul><hr><h2 id="_2-proxyler-vekil-sunucular" tabindex="-1">2. Proxyler (Vekil Sunucular) <a class="header-anchor" href="#_2-proxyler-vekil-sunucular" aria-label="Permalink to &quot;2. Proxyler (Vekil Sunucular)&quot;">​</a></h2><p>İstemci ve sunucu arasındaki aracılardır.</p><h3 id="forward-proxy-ileri-yonlu-vekil" tabindex="-1">Forward Proxy (İleri Yönlü Vekil) <a class="header-anchor" href="#forward-proxy-ileri-yonlu-vekil" aria-label="Permalink to &quot;Forward Proxy (İleri Yönlü Vekil)&quot;">​</a></h3><ul><li><strong>Konumu</strong>: İstemcinin önünde.</li><li><strong>Amacı</strong>: İstemciyi korumak.</li><li><strong>Kullanım Alanı</strong>: Kurumsal ofislerde Facebook&#39;u engelleme, yaygın siteleri önbellekleme, anonimlik (VPN).</li><li><strong>Akış</strong>: İstemci -&gt; Forward Proxy -&gt; İnternet.</li></ul><h3 id="reverse-proxy-ters-yonlu-vekil" tabindex="-1">Reverse Proxy (Ters Yönlü Vekil) <a class="header-anchor" href="#reverse-proxy-ters-yonlu-vekil" aria-label="Permalink to &quot;Reverse Proxy (Ters Yönlü Vekil)&quot;">​</a></h3><ul><li><strong>Konumu</strong>: Sunucunun önünde.</li><li><strong>Amacı</strong>: Sunucuyu korumak.</li><li><strong>Kullanım Alanı</strong>: Yük dengeleme, SSL Sonlandırma, Önbellekleme, backend topolojisini gizleme.</li><li><strong>Akış</strong>: İnternet -&gt; Reverse Proxy (Nginx) -&gt; Uygulama Sunucusu (Node.js).</li></ul><blockquote><p><strong>DevOps Standardı</strong>: Neredeyse TÜM web uygulamaları bir reverse proxy (Nginx, Traefik, HAProxy veya Cloud LB) arkasında çalışır.</p></blockquote><hr><h2 id="_3-ssl-sonlandırma-ssl-termination" tabindex="-1">3. SSL Sonlandırma (SSL Termination) <a class="header-anchor" href="#_3-ssl-sonlandırma-ssl-termination" aria-label="Permalink to &quot;3. SSL Sonlandırma (SSL Termination)&quot;">​</a></h2><p>HTTPS trafiğinin şifresini Load Balancer/Proxy seviyesinde çözerek backend sunucusunun sadece HTTP ile uğraşmasını sağlamaktır.</p><ul><li><strong>Avantajları</strong>: Şifreleme yükünü uygulama sunucularından alır. Merkezi sertifika yönetimi sağlar.</li><li><strong>Dezavantajları</strong>: LB ve Uygulama arasındaki trafik şifresizdir (özel VPC içinde genellikle sorun değildir).</li></ul>',19)])])}const k=e(n,[["render",i]]);export{c as __pageData,k as default};
