import{_ as e,o as a,c as l,ag as r}from"./chunks/framework.DhRkhqU-.js";const m=JSON.parse('{"title":"Güvenlik Duvarları & Güvenlik: Ağ Savunması","description":"","frontmatter":{},"headers":[],"relativePath":"08-Network/05-Firewalls-Security.md","filePath":"08-Network/05-Firewalls-Security.md"}'),i={name:"08-Network/05-Firewalls-Security.md"};function n(s,t,o,u,d,g){return a(),l("div",null,[...t[0]||(t[0]=[r('<h1 id="guvenlik-duvarları-guvenlik-ag-savunması" tabindex="-1">Güvenlik Duvarları &amp; Güvenlik: Ağ Savunması <a class="header-anchor" href="#guvenlik-duvarları-guvenlik-ag-savunması" aria-label="Permalink to &quot;Güvenlik Duvarları &amp; Güvenlik: Ağ Savunması&quot;">​</a></h1><h2 id="_1-guvenlik-duvarları-firewalls" tabindex="-1">1. Güvenlik Duvarları (Firewalls) <a class="header-anchor" href="#_1-guvenlik-duvarları-firewalls" aria-label="Permalink to &quot;1. Güvenlik Duvarları (Firewalls)&quot;">​</a></h2><p>Ağ trafiğini kurallara göre kontrol eden cihazlar veya yazılımlardır.</p><h3 id="stateful-vs-stateless" tabindex="-1">Stateful vs Stateless <a class="header-anchor" href="#stateful-vs-stateless" aria-label="Permalink to &quot;Stateful vs Stateless&quot;">​</a></h3><ul><li><strong>Stateless (Durumsuz - ACL&#39;ler)</strong>: Paketleri tek tek kontrol eder. Gelen isteğe izin verirseniz, giden yanıtı da açıkça (explicitly) izin vermelisiniz. <ul><li><em>Örnek</em>: AWS Network ACLs (NACL).</li></ul></li><li><strong>Stateful (Durumlu)</strong>: Bağlantıları hatırlar. Gelen isteğe (port 80) izin verirseniz, dönüş trafiğine otomatik olarak izin verir. <ul><li><em>Örnek</em>: AWS Security Groups, <code>iptables</code>.</li></ul></li></ul><hr><h2 id="_2-bulut-guvenligi-aws-baglamı" tabindex="-1">2. Bulut Güvenliği (AWS Bağlamı) <a class="header-anchor" href="#_2-bulut-guvenligi-aws-baglamı" aria-label="Permalink to &quot;2. Bulut Güvenliği (AWS Bağlamı)&quot;">​</a></h2><p>Aradaki farkı anlamak DevOps için kritiktir.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Özellik</th><th style="text-align:left;">Security Group (SG)</th><th style="text-align:left;">Network ACL (NACL)</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Seviye</strong></td><td style="text-align:left;">Instance (EC2)</td><td style="text-align:left;">Subnet</td></tr><tr><td style="text-align:left;"><strong>Tür</strong></td><td style="text-align:left;">Stateful</td><td style="text-align:left;">Stateless</td></tr><tr><td style="text-align:left;"><strong>Kurallar</strong></td><td style="text-align:left;">Sadece İzin Ver (Varsayılan Red)</td><td style="text-align:left;">İzin Ver ve Reddet</td></tr><tr><td style="text-align:left;"><strong>Sıra</strong></td><td style="text-align:left;">Tüm kurallar değerlendirilir</td><td style="text-align:left;">Numaralı sıra (100, 200)</td></tr><tr><td style="text-align:left;"><strong>Kullanım</strong></td><td style="text-align:left;">Uygulamalar için birincil duvar</td><td style="text-align:left;">Ekstra katman (IP engelleme)</td></tr></tbody></table><hr><h2 id="_3-vpn-sanal-ozel-ag" tabindex="-1">3. VPN (Sanal Özel Ağ) <a class="header-anchor" href="#_3-vpn-sanal-ozel-ag" aria-label="Permalink to &quot;3. VPN (Sanal Özel Ağ)&quot;">​</a></h2><p>Özel bir ağı genel bir ağ üzerinden genişletmek.</p><ul><li><strong>Site-to-Site</strong>: Bir Ofis Router&#39;ını Bulut VPC&#39;sine bağlar. Sunucular sunucularla özel olarak konuşur.</li><li><strong>Client-to-Site</strong>: Uzaktan çalışan geliştirici laptopunu Ofis/Bulut ağına bağlar.</li></ul><hr><h2 id="_4-ddos-dagıtık-hizmet-reddi" tabindex="-1">4. DDOS (Dağıtık Hizmet Reddi) <a class="header-anchor" href="#_4-ddos-dagıtık-hizmet-reddi" aria-label="Permalink to &quot;4. DDOS (Dağıtık Hizmet Reddi)&quot;">​</a></h2><ul><li><strong>Hacimsel (Volumetric)</strong>: Bant genişliğini doldurma (UDP selleri).</li><li><strong>Protokol</strong>: Sunucu kaynaklarını tüketme (SYN Flood).</li><li><strong>Uygulama</strong>: CPU&#39;yu öldürmek için karmaşık istekler (Arama sorguları).</li></ul><p><strong>Azaltma (Mitigation)</strong>:</p><ul><li><strong>CDN</strong>: Cloudflare/CloudFront trafiği emer.</li><li><strong>WAF (Web Application Firewall)</strong>: Zararlı HTTP desenlerini engeller (SQL Injection).</li><li><strong>Autoscaling</strong>: Yükü karşılar (pahalıdır).</li></ul>',18)])])}const v=e(i,[["render",n]]);export{m as __pageData,v as default};
