import{_ as r,o as a,c as e,ag as l}from"./chunks/framework.DhRkhqU-.js";const c=JSON.parse('{"title":"Network İleri Kavramlar","description":"","frontmatter":{},"headers":[],"relativePath":"08-Network/08-Advanced-Concepts.md","filePath":"08-Network/08-Advanced-Concepts.md"}'),t={name:"08-Network/08-Advanced-Concepts.md"};function n(o,i,s,g,d,u){return a(),e("div",null,[...i[0]||(i[0]=[l('<h1 id="network-ileri-kavramlar" tabindex="-1">Network İleri Kavramlar <a class="header-anchor" href="#network-ileri-kavramlar" aria-label="Permalink to &quot;Network İleri Kavramlar&quot;">​</a></h1><h2 id="_1-stp-rstp-ve-edge-port-davranısı" tabindex="-1">1) STP / RSTP ve Edge Port Davranışı <a class="header-anchor" href="#_1-stp-rstp-ve-edge-port-davranısı" aria-label="Permalink to &quot;1) STP / RSTP ve Edge Port Davranışı&quot;">​</a></h2><ul><li><strong>Bridge ID</strong>: <code>Bridge Priority (2 byte) + MAC Address (6 byte)</code> → toplam 8 byte. Önce priority, eşitse en düşük MAC kök olur.</li><li><strong>Bridge Priority</strong>: Varsayılan 32768, 4096&#39;lık adımlarla değiştirilir. Root Bridge seçimini belirler.</li><li><strong>Root Bridge Seçimi</strong>: Öncelik sırasıyla <code>düşük priority</code>, ardından <code>düşük MAC</code>.</li><li><strong>RSTP Edge Port</strong>: Uç cihazlara (PC, printer vb.) giden ve direkt Forwarding’e geçen port.</li><li><strong>Edge Port BPDU Alırsa</strong>: Normal RSTP portuna döner; geçiş sürecine ve olası blocking’e girer.</li><li><strong>BPDU Guard</strong>: Edge port BPDU alınca portu err-disable yapar; loop riskini engeller.</li><li><strong>BPDU Filter</strong>: BPDU gönderimini/alımını bastırır; yanlış kullanılırsa loop riski doğar.</li></ul><h2 id="_2-dhcp-ozel-alanları-ve-havuz-yonetimi" tabindex="-1">2) DHCP Özel Alanları ve Havuz Yönetimi <a class="header-anchor" href="#_2-dhcp-ozel-alanları-ve-havuz-yonetimi" aria-label="Permalink to &quot;2) DHCP Özel Alanları ve Havuz Yönetimi&quot;">​</a></h2><ul><li><strong>Option 66</strong>: TFTP sunucu adı/domain bilgisi.</li><li><strong>Option 150</strong>: (Cisco ağırlıklı) TFTP sunucusunun IP adresi.</li><li><strong>Reservation</strong>: Belirli MAC’e hep aynı IP’yi ver.</li><li><strong>Exclusion Range</strong>: Havuzdan dağıtılmayacak IP aralığı.</li><li><strong>Scope / Adres Havuzu</strong>: Belirli bir subnet için dağıtılabilir IP seti.</li><li><strong>Superscope</strong>: Birden fazla scope’u aynı mantıksal grup altında toplar.</li></ul><h2 id="_3-dns-kayıtları-ve-zone-lar" tabindex="-1">3) DNS Kayıtları ve Zone’lar <a class="header-anchor" href="#_3-dns-kayıtları-ve-zone-lar" aria-label="Permalink to &quot;3) DNS Kayıtları ve Zone’lar&quot;">​</a></h2><ul><li><strong>Forward vs Reverse Lookup Zone</strong>: Forward → isimden IP; Reverse → IP’den isim.</li><li><strong>PTR Kaydı</strong>: Sadece Reverse Lookup Zone içinde kullanılır; IP → isim eşlemesi.</li><li><strong>SOA (Start of Authority)</strong>: Zone’un otorite başlangıcını tanımlar; tüm DNS sunucularını değil, authoritative kaynağı belirtir.</li><li><strong>NS (Name Server)</strong>: Zone için yetkili DNS sunucularını listeler.</li></ul><h2 id="_4-aaa-ve-ilgili-protokoller" tabindex="-1">4) AAA ve İlgili Protokoller <a class="header-anchor" href="#_4-aaa-ve-ilgili-protokoller" aria-label="Permalink to &quot;4) AAA ve İlgili Protokoller&quot;">​</a></h2><ul><li><strong>AAA</strong>: Authentication, Authorization, Accounting.</li><li><strong>RADIUS</strong>: AAA için UDP tabanlı; auth+acct birleşik; şifreleme sadece kimlik bilgisi alanlarında.</li><li><strong>TACACS+</strong>: AAA için TCP tabanlı; auth/author/acct ayrık; tüm payload şifreli.</li><li><strong>Diameter</strong>: Modern, TCP/SCTP tabanlı; RADIUS’un yerini almaya yönelik.</li><li><strong>AAA olmayanlar (karıştırmayın)</strong>: DHCP, DNS, SNMP, OSPF, FTP, GRE, SSH vb.</li></ul><h2 id="_5-multicast-uyeligi" tabindex="-1">5) Multicast Üyeliği <a class="header-anchor" href="#_5-multicast-uyeligi" aria-label="Permalink to &quot;5) Multicast Üyeliği&quot;">​</a></h2><ul><li><strong>IGMP</strong>: IPv4 multicast grup üyeliği yönetimi.</li><li><strong>MLD</strong>: IPv6 multicast grup üyeliği (ICMPv6 tabanlı).</li><li><strong>IGMP vs MLD</strong>: IPv4 vs IPv6 ayrımı; işlev benzer.</li><li><strong>Katılım Süreci</strong>: İstemci IGMP/MLD report gönderir; router/switch buna göre forwarding ağaçlarını günceller.</li></ul><h2 id="_6-vlan-temelleri" tabindex="-1">6) VLAN Temelleri <a class="header-anchor" href="#_6-vlan-temelleri" aria-label="Permalink to &quot;6) VLAN Temelleri&quot;">​</a></h2><ul><li><strong>Management VLAN</strong>: Switch yönetimi için kullanılan VLAN.</li><li><strong>Default VLAN</strong>: Varsayılan genelde VLAN 1.</li><li><strong>Native VLAN</strong>: 802.1Q trunk üzerinde tag’siz trafiğin ait olduğu VLAN.</li><li><strong>Access Port</strong>: Tek VLAN üyesi; frame’ler tag’siz taşınır.</li><li><strong>Trunk Port</strong>: Birden fazla VLAN trafiğini tag’li taşır.</li><li><strong>“Akışkan/Fluid VLAN”</strong>: Standart bir VLAN türü değildir; yanlış kullanımdan kaçının.</li></ul><h2 id="_7-tunelleme-ve-multicast" tabindex="-1">7) Tünelleme ve Multicast <a class="header-anchor" href="#_7-tunelleme-ve-multicast" aria-label="Permalink to &quot;7) Tünelleme ve Multicast&quot;">​</a></h2><ul><li><strong>GRE (Generic Routing Encapsulation)</strong>: Katman 3 tünelleme; multicast/broadcast’ı doğal olarak keşfetmez.</li><li><strong>Multicast Dinleyiciler</strong>: IGMP/MLD ile router’a katılım bildirir; GRE tüneli üzerinden otomatik keşif beklenmez.</li></ul><h2 id="_8-vlan-yayılım-protokolleri" tabindex="-1">8) VLAN Yayılım Protokolleri <a class="header-anchor" href="#_8-vlan-yayılım-protokolleri" aria-label="Permalink to &quot;8) VLAN Yayılım Protokolleri&quot;">​</a></h2><ul><li><strong>GVRP (GARP VLAN Registration Protocol)</strong>: IEEE 802.1Q içinde; switch’ler arası VLAN membership bilgisini yayar (standart).</li><li><strong>VTP (VLAN Trunking Protocol)</strong>: Cisco’ya özel; VLAN database yönetimini merkezileştirir.</li><li><strong>GVRP vs VTP</strong>: Standart vs proprietary; membership yayılımı vs VLAN database çoğaltma farkı.</li></ul><h2 id="_9-guvenilirlik-ve-mtu" tabindex="-1">9) Güvenilirlik ve MTU <a class="header-anchor" href="#_9-guvenilirlik-ve-mtu" aria-label="Permalink to &quot;9) Güvenilirlik ve MTU&quot;">​</a></h2><ul><li><strong>Ağ Güvenilirliği</strong>: Segmentler kaybolabilir, bozulabilir, sırası değişebilir.</li><li><strong>TCP Mekanizmaları</strong>: Sequence number, ACK, retransmission, windowing ile güvenilirlik sağlar.</li><li><strong>MTU (Maximum Transmission Unit)</strong>: Çerçeve boyut limiti; üstünde fragmentation gerekebilir.</li><li><strong>İlişkili Kavramlar</strong>: Fragmentation, MSS, Path MTU Discovery (PMTUD) ile yol üzerindeki en düşük MTU’nun tespiti.</li></ul>',19)])])}const k=r(t,[["render",n]]);export{c as __pageData,k as default};
