import{_ as e,o as r,c as i,ag as l}from"./chunks/framework.DhRkhqU-.js";const m=JSON.parse('{"title":"Bulut Sözlüğü: Veritabanı (Database)","description":"","frontmatter":{},"headers":[],"relativePath":"11-Cloud-Dictionary/04-Database.md","filePath":"11-Cloud-Dictionary/04-Database.md"}'),n={name:"11-Cloud-Dictionary/04-Database.md"};function t(s,a,o,u,b,d){return r(),i("div",null,[...a[0]||(a[0]=[l('<h1 id="bulut-sozlugu-veritabanı-database" tabindex="-1">Bulut Sözlüğü: Veritabanı (Database) <a class="header-anchor" href="#bulut-sozlugu-veritabanı-database" aria-label="Permalink to &quot;Bulut Sözlüğü: Veritabanı (Database)&quot;">​</a></h1><p>Veriyi yapısal olarak saklama ve sorgulama yöntemleri.</p><h2 id="_1-iliskisel-veritabanları-rdbms-sql" tabindex="-1">1. İlişkisel Veritabanları (RDBMS / SQL) <a class="header-anchor" href="#_1-iliskisel-veritabanları-rdbms-sql" aria-label="Permalink to &quot;1. İlişkisel Veritabanları (RDBMS / SQL)&quot;">​</a></h2><p><strong>AWS RDS, Azure SQL, Google Cloud SQL</strong></p><p>Tablolar, satırlar ve sütunlardan oluşur. Veriler birbirine sıkı sıkıya bağlıdır (İlişkisel).</p><ul><li><strong>Motorlar:</strong> MySQL, PostgreSQL, Oracle, SQL Server.</li><li><strong>Yönetilen Servis (Managed):</strong> Bulut sağlayıcısı yedeklemeyi, güncellemeyi ve ölçeklemeyi sizin yerinize yapar. Siz sadece kullanırsınız.</li><li><strong>Kullanım:</strong> E-ticaret siteleri, Finans uygulamaları, Müşteri verileri.</li></ul><h2 id="_2-nosql-veritabanları" tabindex="-1">2. NoSQL Veritabanları <a class="header-anchor" href="#_2-nosql-veritabanları" aria-label="Permalink to &quot;2. NoSQL Veritabanları&quot;">​</a></h2><p><strong>AWS DynamoDB, Azure Cosmos DB, Google Firestore</strong></p><p>Esnek şemaya sahiptir. Tablo yerine JSON benzeri dokümanlar veya Anahtar-Değer (Key-Value) çiftleri kullanılır.</p><ul><li><strong>Özellik:</strong> Çok hızlıdır ve yatayda sınırsız büyüyebilir.</li><li><strong>Kullanım:</strong> Oyun skor tabloları, Sosyal medya akışları, Sepet verisi.</li></ul><h2 id="_3-bellek-ici-onbellek-in-memory-caching" tabindex="-1">3. Bellek İçi Önbellek (In-Memory Caching) <a class="header-anchor" href="#_3-bellek-ici-onbellek-in-memory-caching" aria-label="Permalink to &quot;3. Bellek İçi Önbellek (In-Memory Caching)&quot;">​</a></h2><p><strong>AWS ElastiCache, Azure Redis Cache</strong></p><p>Veriyi disk yerine RAM&#39;de tutar. Milisaniyenin altında (mikrosaniye) yanıt verir.</p><ul><li><strong>Kullanım:</strong> Çok sık sorulan sorguları (örn: &quot;En çok satan ürünler&quot;) veritabanına gitmeden RAM&#39;den hızlıca getirmek için.</li></ul><h2 id="_4-veritabanı-tasıma-migration" tabindex="-1">4. Veritabanı Taşıma (Migration) <a class="header-anchor" href="#_4-veritabanı-tasıma-migration" aria-label="Permalink to &quot;4. Veritabanı Taşıma (Migration)&quot;">​</a></h2><p><strong>AWS DMS, Azure Database Migration Service</strong></p><p>Kendi sunucunuzdaki (On-Premise) veritabanını buluta kesintisiz taşımak için kullanılan araçlardır.</p><ul><li><strong>Özellik:</strong> Kaynak veritabanı çalışmaya devam ederken veriyi kopyalar, son andaki değişiklikleri de senkronize eder.</li></ul>',18)])])}const g=e(n,[["render",t]]);export{m as __pageData,g as default};
