import{_ as a,o as n,c as r,ag as t}from"./chunks/framework.DhRkhqU-.js";const g=JSON.parse('{"title":"Bulut Sözlüğü: Ağ (Networking)","description":"","frontmatter":{},"headers":[],"relativePath":"11-Cloud-Dictionary/05-Networking.md","filePath":"11-Cloud-Dictionary/05-Networking.md"}'),i={name:"11-Cloud-Dictionary/05-Networking.md"};function l(o,e,d,u,s,c){return n(),r("div",null,[...e[0]||(e[0]=[t('<h1 id="bulut-sozlugu-ag-networking" tabindex="-1">Bulut Sözlüğü: Ağ (Networking) <a class="header-anchor" href="#bulut-sozlugu-ag-networking" aria-label="Permalink to &quot;Bulut Sözlüğü: Ağ (Networking)&quot;">​</a></h1><p>Buluttaki sanal kablolar ve trafik polisleri.</p><h2 id="_1-sanal-ag-vpc-vnet" tabindex="-1">1. Sanal Ağ (VPC / VNet) <a class="header-anchor" href="#_1-sanal-ag-vpc-vnet" aria-label="Permalink to &quot;1. Sanal Ağ (VPC / VNet)&quot;">​</a></h2><p><strong>AWS VPC, Azure VNet, Google VPC</strong></p><p>Bulutun içinde size ayrılmış, izole edilmiş özel bir alandır. Evinizdeki yerel ağ (LAN) gibidir ama buluttadır.</p><ul><li><strong>Alt Ağ (Subnet):</strong> VPC&#39;yi daha küçük parçalara böler (Public Subnet: İnternete açık, Private Subnet: İnternete kapalı).</li></ul><h2 id="_2-yuk-dengeleyici-load-balancer" tabindex="-1">2. Yük Dengeleyici (Load Balancer) <a class="header-anchor" href="#_2-yuk-dengeleyici-load-balancer" aria-label="Permalink to &quot;2. Yük Dengeleyici (Load Balancer)&quot;">​</a></h2><p><strong>AWS ELB, Azure Load Balancer</strong></p><p>Gelen trafiği birden fazla sunucuya dağıtır.</p><ul><li><strong>Layer 4 (Network):</strong> IP ve Port&#39;a bakar. Çok hızlıdır.</li><li><strong>Layer 7 (Application):</strong> URL&#39;e bakar (<code>/api</code> sunucu A&#39;ya, <code>/images</code> sunucu B&#39;ye). Daha akıllıdır.</li></ul><h2 id="_3-cdn-content-delivery-network" tabindex="-1">3. CDN (Content Delivery Network) <a class="header-anchor" href="#_3-cdn-content-delivery-network" aria-label="Permalink to &quot;3. CDN (Content Delivery Network)&quot;">​</a></h2><p><strong>AWS CloudFront, Azure CDN, Google Cloud CDN</strong></p><p>Web sitenizin statik dosyalarını (resim, CSS, JS) dünyanın her yerindeki sunuculara (Edge Locations) kopyalar.</p><ul><li><strong>Fayda:</strong> Türkiye&#39;den giren kullanıcı dosyayı Amerika&#39;dan değil, İstanbul&#39;daki sunucudan çeker. Site çok hızlanır.</li></ul><h2 id="_4-dns-domain-name-system" tabindex="-1">4. DNS (Domain Name System) <a class="header-anchor" href="#_4-dns-domain-name-system" aria-label="Permalink to &quot;4. DNS (Domain Name System)&quot;">​</a></h2><p><strong>AWS Route 53, Azure DNS</strong></p><p>Alan adınızı (<code>site.com</code>) yönetir ve trafiği doğru yere yönlendirir.</p><ul><li><strong>Özellik:</strong> Sadece isim çözmez, aynı zamanda sunucunun sağlıklı olup olmadığını (Health Check) kontrol eder. Sunucu çökerse trafiği yedek sunucuya çevirir.</li></ul><h2 id="_5-ozel-hat-dedicated-line" tabindex="-1">5. Özel Hat (Dedicated Line) <a class="header-anchor" href="#_5-ozel-hat-dedicated-line" aria-label="Permalink to &quot;5. Özel Hat (Dedicated Line)&quot;">​</a></h2><p><strong>AWS Direct Connect, Azure ExpressRoute</strong></p><p>Şirket ofisiniz ile bulut veri merkezi arasında çekilen fiziksel fiber kablodur.</p><ul><li><strong>Fayda:</strong> İnternet üzerinden gitmez. Çok güvenli, çok hızlı ve düşük gecikmelidir. Bankalar ve büyük şirketler kullanır.</li></ul>',22)])])}const p=a(i,[["render",l]]);export{g as __pageData,p as default};
